import './App.css';

function App() {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  const Header = (props) => {
    return <h1>{props.title}</h1>
  }

  const Part = (props) =>{
    if(props.part === part1.name && props.exercises === part1.exercises){
      return(part1.name+": "+part1.exercises)
    }else if(props.part === part2.name && props.exercises === part2.exercises){
      return(part2.name+": "+part2.exercises)
    }else if(props.part === part3.name && props.exercises === part3.exercises){
      return(part3.name+": "+part3.exercises)
    }
  }

  const Content = () => {
    return(
      <div>
        <Part part={part1.name} exercises={part1.exercises}/>
        <br/>
        <Part part={part2.name} exercises={part2.exercises}/>
        <Part part={part3.name} exercises={part3.exercises}/>
      </div>
    )
  }

  return (
    <div>
      <Header title={course}/>
      <Content/>
    </div>
  )
}

export default App;
