import './App.css';
import React, { useState } from 'react'

const Header = (props) => {
  return(<h1>{props.name}</h1>)
}

const Total = (props) => {
  return(<p>{props.name} {(props.good + props.neutral + props.bad)}</p>)
}

const operador = (func, value) => () => {
  func(value)
}

const Button = (props) => {
  return(
    <button onClick={operador(props.func, props.value + 1)}>{props.name}</button> 
  )
}

const Statistic = (props) =>{
  const {name, good, neutral, bad} = props
}


const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Header name="give feedback"/>
      <Button name="good" func={setGood} value={good}/>
      <Button name="neutral" func={setNeutral} value={neutral}/>
      <Button name="bad" func={setBad} value={bad}/>
      <Header name="statistics"/>
      <Total name="total" good={good} neutral={neutral} bad={bad}/>
    </div>
  )
}

export default App;
