import './App.css';

function App() {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const Part = (props) => {
    if(props.part1 === part1 && props.exercises1 === exercises1){
      return(props.part1 + ": " + props.exercises1)
    }else if(props.part2 === part2 && props.exercises2 === exercises2){
      return(props.part2 + ": " + props.exercises2)
    }else if(props.part3 === part3 && props.exercises3 === exercises3){
      return(props.part3 + ": " + props.exercises3)
    }
  }

  const Header = (props) => {
    console.log(props)
    return props.title
  }

  const Content = (props) => {
    console.log(props)
    return (
      <div>
        <Part part1={part1} exercises1={exercises1}/>
        <br/>
        <Part part2={part2} exercises2={exercises2}/>
        <br/>
        <Part part3={part3} exercises3={exercises3}/>
      </div>
    )
  }

  const Total = (props) => {
    return ("total excercises: " + (props.ex1 + props.ex2 + props.ex3))
  }


  return (
    <div>
      <Header title={course}/>
      <br/>
      <Content/>
      <Total ex1={exercises1} ex2={exercises2} ex3={exercises3}/>
    </div>
  )
}

export default App;
