import './App.css';

function App() {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const Header = (props) => {
    return props.title
  }

  const Content = (props) => {
    return (props.part1 + " " + props.exercises1  + props.part2 + " " + props.exercises2 + props.part3 + " " + props.exercises3)
  }

  const Total = (props) => {
    return ("total excercises: " + (props.ex1 + props.ex2 + props.ex3))
  }


  return (
    <div>
      <Header title={course}/>
      <br/>
      <Content part1={part1} exercises1={exercises1} part2={part2} exercises2={exercises2} part3={part3} exercises3={exercises3}/> 
      <br/>
      <Total ex1={exercises1} ex2={exercises2} ex3={exercises3}/>
    </div>
  )
}

export default App;
